import React from "react";

import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";

import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";

import interactionPlugin, { Draggable } from '@fullcalendar/interaction';

export default function MyCalendar() {
  const events = [{ title: "today's event", date: new Date() }];

    const localizer = momentLocalizer(moment);
        const [eventsList, setEventsList] = useState([]);
        const [alarmTime2, setAlarm] = useState([]);
        const [seconds, setSeconds] = useState([]);

        var state = {
           currentTime: new Date().toLocaleTimeString('ru', { hour12: false }),
           }

        useEffect(() => {
                 const timer = setInterval(() => {
                      setSeconds(
                      state.currentTime )
                 }, 1000);
                 return () => clearInterval(timer);
        });

        function handleSelect({ start, end, userName }) {
            const title = window.prompt("New Event name");
            if (title) {
              var newEvent = {
                userName: {userName},
                start: start,
                end: end,
                title: title,
                alarmTime1: start.toLocaleTimeString('ru', { hour12: false })
              };
              setEventsList([...eventsList, newEvent]);
              setAlarm([...alarmTime2, newEvent.alarmTime1]);
              }
            }
        var counter = 0;
        while(counter != 100){
             if(state.currentTime === alarmTime2[counter]) {
                     alert("its time!");
                     };
              counter = counter + 1;
             }

  return (
      <FullCalendar
        height={750}
        slotDuration={'00:30:00'}
        defaultView="dayGridMonth"
                headerToolbar={{
                  left: "prev,next",
                  center: "title",
                  right: "dayGridMonth,timeGridWeek,timeGridDay"
                }}
        plugins={[dayGridPlugin, timeGridPlugin]}
        events={events}
        onSelectSlot={handleSelect}
        locale="ru"
      />
  );
}
