import React, { useState, Component, useEffect, setState  } from "react";
import { Alert, useAlert } from 'react-alert'
import {userName} from './Home';
import Clock from 'react-clock';
import { momentLocalizer } from 'react-big-calendar'
import moment from 'moment'

import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";

import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";

import 'bootstrap/dist/css/bootstrap.css';
import '@fortawesome/fontawesome-free/css/all.css';

import interactionPlugin, { Draggable } from '@fullcalendar/interaction';
import bootstrapPlugin from '@fullcalendar/bootstrap';

export default function MyCalendar() {

    document.addEventListener('DOMContentLoaded', function() {
        var calendarEl = document.getElementById('calendar');

        var calendar = new FullCalendar.Calendar(calendarEl, {
          plugins: [ 'interaction', 'dayGrid', 'timeGrid', 'list' ],
          header: {
            center: 'addEventButton'
          },
          customButtons: {
            addEventButton: {
              text: 'add event...',
              click: function() {
                var dateStr = prompt('Enter a date in YYYY-MM-DD format');
                var date = new Date(dateStr + 'T00:00:00'); // will be in local time

                if (!isNaN(date.valueOf())) { // valid?
                  calendar.addEvent({
                    title: 'dynamic event',
                    start: date,
                    allDay: true
                  });
                  alert('Great. Now, update your database...');
                } else {
                  alert('Invalid date.');
                }
              }
            }
          }
        });

        calendar.render();
      });

    const [eventsList, setEventsList] = useState([]);
        const [alarmTime2, setAlarm] = useState([]);
        const [seconds, setSeconds] = useState([]);

        var state = {
           currentTime: new Date().toLocaleTimeString('ru', { hour12: false }),
           }

        useEffect(() => {
                 const timer = setInterval(() => {
                      setSeconds(
                      state.currentTime )
                 }, 1000);
                 return () => clearInterval(timer);
        });

        var counter = 0;
        while(counter != 100){
             if(state.currentTime === alarmTime2[counter]) {
                     alert("its time!");
                     };
              counter = counter + 1;
             }

  return (
      <FullCalendar
        firstDay={1}
        navLinks={true}
        nowIndicator={true}
        themeSystem={'bootstrap'}
        height={750}
        slotDuration={'00:30:00'}
        defaultView="dayGridMonth"
                buttonText={{
                      today:    'сегодня',
                      month:    'месяц',
                      week:     'неделя',
                      day:      'день',
                }}
                headerToolbar={{
                  left: "prev today next",
                  center: "title",
                  right: "dayGridMonth,timeGridWeek,timeGridDay"
                }}

        plugins={[dayGridPlugin, timeGridPlugin, bootstrapPlugin ]}
        events={eventsList}
        eventStartEditable={true}

        locale="ru"
      />

  );
}
