import React, { useState, Component, useEffect, setState  } from "react";
import { Alert, useAlert } from 'react-alert'
import {userName} from './Home';
import Clock from 'react-clock';
import { momentLocalizer } from 'react-big-calendar'
import moment from 'moment'

import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from '@fullcalendar/interaction';

import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";

import 'bootstrap/dist/css/bootstrap.css';
import '@fortawesome/fontawesome-free/css/all.css';

import bootstrapPlugin from '@fullcalendar/bootstrap';

export default function MyCalendar() {

    const [eventsList, setEventsList] = useState([]);
        const [alarmTime2, setAlarm] = useState([]);
        const [seconds, setSeconds] = useState([]);

        var state = {
           currentTime: new Date().toLocaleTimeString('ru', { hour12: false }),
           }

        useEffect(() => {
                 const timer = setInterval(() => {
                      setSeconds(
                      state.currentTime )
                 }, 1000);
                 return () => clearInterval(timer);
        });

        var counter = 0;
        while(counter != 100){
             if(state.currentTime === alarmTime2[counter]) {
                     alert("its time!");
                     };
              counter = counter + 1;
             }

  return (
      <FullCalendar
        firstDay={1}
        navLinks={true}
        nowIndicator={true}
        themeSystem={'bootstrap'}
        height={750}
        slotDuration={'00:30:00'}
        defaultView="dayGridMonth"
                buttonText={{
                      today:    'сегодня',
                      month:    'месяц',
                      week:     'неделя',
                      day:      'день',
                }}
                headerToolbar={{
                  left: "prev today next",
                  center: "title",
                  right: "dayGridMonth,timeGridWeek,timeGridDay"
                }}
                footerToolbar={{
                    left: "myCustomButton"
                }}
                customButtons={{
                        myCustomButton: {
                            text: 'add event...',
                                    click: function() {
                                      var startDateStr = prompt('Enter a START date in YYYY-MM-DD T00:00:00 format');
                                      var startDate = new Date(startDateStr); // will be in local time
                                      var endDateStr = prompt('Enter an END date in YYYY-MM-DD T00:00:00 format');
                                      var endDate = new Date(endDateStr); // will be in local time
                                      const title = window.prompt("New Event name");
                                      if (title) {
                                      if (!isNaN(startDate.valueOf())) { // valid?
                                      if (!isNaN(endDate.valueOf())) { // valid?
                                        var addEvent ={
                                          title: title,
                                          start: startDate,
                                          end: endDate,
                                          userName: {userName},
                                          alarmTime1: startDate.toLocaleTimeString('ru', { hour12: false })
                                        };
                                        alert('Great. Now, update your database...');
                                        setEventsList([...eventsList, addEvent]);
                                        setAlarm([...alarmTime2, addEvent.alarmTime1]);
                                      } else {
                                        alert('Invalid date.');
                                        }
                                      }
                                      }
                                    },
                        },
                    }}

        plugins={[dayGridPlugin, timeGridPlugin, bootstrapPlugin, interactionPlugin ]}
        events={eventsList}
        editable={true}
        droppable={true}
        eventResizableFromStart={true}
        locale="ru"
      />

  );
}
